#define  MemBlock  (20000)

long mptr;
char *Memory;
FILE *f;

void initwriting(file)
{
	mptr=0;
	Memory=(char*)mxMalloc(MemBlock);
	f=file;
}
void emptybuffer()
{
	fwrite(Memory,mptr,1,f);
	

	mptr=0;
}

void writebuffered(str)
{
	int wlen=strlen(str);
	
	if(wlen+mptr<MemBlock)
	{
		strcpy(Memory+mptr,str);
		mptr+=wlen;
	}else
	{
		emptybuffer();
		
		
		strcpy(Memory,str);
		mptr+=wlen;
	}
}

void endwriting()
{
	if(mptr>0)
	{
		fwrite(Memory,mptr,1,f);
	}
	
	mxFree(Memory);
	mexPrintf("Done!\n");	
}


