function shiftedy = shift_signal(y, shiftby, interp_method)% :Usage:% ::%%     [shiftedy] = shift_signal(y, shiftby, [interpolation method])%% Shifts signal contained in y back 'shiftby' steps%% Note the variable shiftby should be positive.    if(~isvector(y))        error('shift_signal can only shift vectors');    end        shiftby = abs(shiftby);    if(~exist('interp_method', 'var') || isempty(interp_method))        interp_method = 'linear';    end        if(mod(shiftby, 1) ~= 0)        shiftedy = interp1(y, (shiftby+1):length(y), interp_method);    else        shiftedy = y(shiftby+1:end);    end        if size(shiftedy,2)~=1, shiftedy = shiftedy'; endend